figs &lt;- list()
for (blog in data$url_blog)
{
  pag &lt;- tryCatch(
    {
      pag &lt;- read_html(blog)
    },
    error = function(e)
    {
      pag &lt;- NA
#      cat("Ocurrió un error:", e$message, "\n")
      return(pag)
    }
  )
