    str_replace("(\\w)(//)(\\w)", "\\1/\\3") |&gt; 
    image_read() |&gt; 
    image_scale("100x100&gt;")

    titulo &lt;- paste0("img/", str_extract(blog, "(?&lt;=//)(.*?)(?=\\.net)"), ".png")
    image_write(img, titulo)

    figs &lt;- append(figs, titulo)
  } else 
  {
    img &lt;- image_read("img/blog-caido.png") |&gt; 
      image_scale("100x100&gt;")

    titulo &lt;- paste0("img/", str_extract(blog, "(?&lt;=//)(.*?)(?=\\.net)"), ".png")

    image_write(img, titulo)

    figs &lt;- append(figs, titulo)
  }
}


